import Header from "./../com/Header";
import ListTable from "./../com/ListTable";
export default {
  name: "goods",
  data() {
    return {
      computedHeight: 0,
    };
  },
  components: {
    "goods-header": Header,
    "list-table": ListTable,
  },
  methods: {
    createActivity() {
      // 创建活动
    },
        getContentH() {
      let datas = [];
      if (this.$refs.headers) datas.push(this.$refs.headers);
      if (this.$refs.pagination) datas.push(this.$refs.pagination);
      if (this.$refs.searchForm) datas.push(this.$refs.searchForm);
      this.computedHeight = this.$util.resizeElementH(datas)-100;
    },
  },
  // 生命周期 - 挂载完成（可以访问DOM元素）
  mounted() {
    // 动态设置子组件高度
    this.$nextTick(() => {
      this.getContentH();
    });
    // 监听浏览器高度变化计算内容高度
    window.addEventListener("resize", () => {
      this.getContentH();
    });
  },
};
