import { mapState, mapMutations } from 'vuex';
import { get } from 'lodash';

export default {
    name: 'log',
    data () {
        return {
            columns: [
                {
                    title: '时间',
                    key: 'time',
                    width: 180
                },
                {
                    title: '信息',
                    key: 'message',
                    minWidth: 300
                },
                {
                    title: '触发页面',
                    slot: 'page',
                    minWidth: 300
                },
                {
                    title: '类型',
                    width: 100,
                    slot: 'type'
                },
                {
                    title: '详细信息',
                    width: 100,
                    slot: 'more'
                }
            ]
        }
    },
    computed: {
        ...mapState('admin/log', [
            'log'
        ])
    },
    methods: {
        ...mapMutations('admin/log', [
            'clean'
        ]),
        get,
        handleMore (log) {
            this.$Notice.info({
                title: '提示',
                desc: '请在浏览器控制台查看完整日志'
            });
            this.$log.capsule('iView Admin', '完整日志内容', 'primary');
            console.group('完整日志');
            console.log('message ', log.message);
            console.log('time: ', log.time);
            console.log('type: ', log.type);
            console.log('meta: ', log.meta);
            console.groupEnd();
        }
    }
}
